SELECT CNLT_SEQ , CNLT_DETAIL_NO , CNLT_PROC_CD , CNLT_PROC_NM , CNLT_DATE , TELL_NUM , USER_NM , CHNL_CODE , CHNL_CODE_NAME , LCODE , LCODE_NAME , MCODE , MCODE_NAME , SCODE , SCODE_NAME , REG_DATE , CUST_NM , RSRV_DATE , REC_URL , IO_TYPE , TARGET_TB , RNUM FROM ( SELECT CNLT_SEQ , CNLT_DETAIL_NO , CNLT_PROC_CD , CNLT_PROC_NM , CNLT_DATE , TELL_NUM , USER_NM , CHNL_CODE , CHNL_CODE_NAME , LCODE , LCODE_NAME , MCODE , MCODE_NAME , SCODE , SCODE_NAME , REG_DATE , CUST_NM , RSRV_DATE , REC_URL , IO_TYPE ,TARGET_TB , ROWNUM AS RNUM FROM ( SELECT A.CNLT_SEQ , A.CNLT_DETAIL_NO , A.CNLT_PROC_CD , (SELECT CODE_NAME FROM TH_CODE WHERE LCODE='001' AND MCODE=A.CNLT_PROC_CD) AS CNLT_PROC_NM , A.CNLT_DATE , A.TELL_NUM , C.USER_NM AS USER_NM , A.CHNL_CODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE='00' AND MCODE='00' AND SCODE='00') AS CHNL_CODE_NAME , A.LCODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE=A.LCODE AND LCODE <> '00' AND MCODE='00' AND SCODE='00') AS LCODE_NAME , A.MCODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE=A.LCODE AND MCODE=A.MCODE AND MCODE <> '00' AND SCODE='00') AS MCODE_NAME , A.SCODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE=A.LCODE AND MCODE=A.MCODE AND SCODE=A.SCODE AND SCODE <> '00') AS SCODE_NAME , A.REG_DATE , B.CUST_NM , A.RSRV_DATE , A.REC_URL , A.IO_TYPE , A.TARGET_TB FROM (SELECT A1.*, 'ORG' AS TARGET_TB FROM TH_CONSULT_DETAIL A1 UNION ALL SELECT A2.*, 'HIST' AS TARGET_TB FROM TH_CONSULT_DETAIL_HIST A2) A , (SELECT B1.* FROM TH_CONSULT B1 UNION ALL SELECT B2.* FROM TH_CONSULT_HIST B2) B ,IF_CO_USER_MA C WHERE 1=1 AND A.CNLT_SEQ = B.CNLT_SEQ AND A.USER_ID = C.USER_ID
AND B.CUST_NM LIKE '%'|| #{selCustNm} ||'%'
AND A.TELL_NUM LIKE '%'|| #{selTellNum} ||'%'
AND C.USER_NM LIKE '%'|| #{selUserNm} ||'%'
AND A.CHNL_CODE = #{selChnlCode}
AND A.LCODE = #{selLcode}
AND A.MCODE = #{selMcode}
AND A.SCODE = #{selScode}
AND A.CNLT_PROC_CD = #{selCnltProcCd}
AND A.CNLT_DATE BETWEEN TO_DATE(#{selFromDt},'YYYY-MM-DD') AND (TO_DATE(#{selToDt},'YYYY-MM-DD') +1)
ORDER BY A.CNLT_DATE DESC ) ) WHERE RNUM BETWEEN #{fromRowPerPage} AND #{toRowPerPage}

SELECT COUNT(*) AS TOTALCNT FROM (SELECT * FROM TH_CONSULT_DETAIL UNION ALL SELECT * FROM TH_CONSULT_DETAIL_HIST) A , (SELECT * FROM TH_CONSULT UNION ALL SELECT * FROM TH_CONSULT_HIST) B, IF_CO_USER_MA C WHERE 1=1 AND A.CNLT_SEQ = B.CNLT_SEQ AND A.USER_ID = C.USER_ID
AND B.CUST_NM LIKE '%'|| #{selCustNm} ||'%'
AND A.TELL_NUM LIKE '%'|| #{selTellNum} ||'%'
AND C.USER_NM LIKE '%'|| #{selUserNm} ||'%'
AND A.CHNL_CODE = #{selChnlCode}
AND A.LCODE = #{selLcode}
AND A.MCODE = #{selMcode}
AND A.SCODE = #{selScode}
AND A.CNLT_PROC_CD = #{selCnltProcCd}
AND A.CNLT_DATE BETWEEN TO_DATE(#{selFromDt},'YYYY-MM-DD') AND (TO_DATE(#{selToDt},'YYYY-MM-DD') +1)

          SELECT SCODE
                   , CODE_NAME
           FROM TH_CONSULT_CODE
         WHERE 1=1

             AND CHNL_CODE =  #{chnlCode}
             AND LCODE = #{lcode}
             AND MCODE= #{mcode}
             AND SCODE<>'00'
             AND USE_YN ='Y'

ORDER BY SCODE ASC

SELECT A.CNLT_SEQ , A.CNLT_DETAIL_NO , A.CNLT_PROC_CD , (SELECT CODE_NAME FROM TH_CODE WHERE LCODE='001' AND MCODE=A.CNLT_PROC_CD) AS CNLT_PROC_NM , A.CNLT_DATE , A.TELL_NUM , C.USER_NM AS USER_NM , A.CHNL_CODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE='00' AND MCODE='00' AND SCODE='00') AS CHNL_CODE_NAME , A.LCODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE=A.LCODE AND LCODE <> '00' AND MCODE='00' AND SCODE='00') AS LCODE_NAME , A.MCODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE=A.LCODE AND MCODE=A.MCODE AND MCODE <> '00' AND SCODE='00') AS MCODE_NAME , A.SCODE , (SELECT CODE_NAME FROM TH_CONSULT_CODE WHERE CHNL_CODE = A.CHNL_CODE AND LCODE=A.LCODE AND MCODE=A.MCODE AND SCODE=A.SCODE AND SCODE <> '00') AS SCODE_NAME , A.REG_DATE , B.CUST_NM , A.RSRV_DATE , A.RECV_NOTE , A.PROC_NOTE , A.TRAN_ID , (SELECT USER_NM FROM IF_CO_USER_MA WHERE USER_ID = A.TRAN_ID) AS TRAN_NM , A.CNTR_NO , A.IO_TYPE FROM (SELECT A1.*, 'ORG' AS TARGET_TB FROM TH_CONSULT_DETAIL A1 UNION ALL SELECT A2.*, 'HIST' AS TARGET_TB FROM TH_CONSULT_DETAIL_HIST A2) A , (SELECT B1.* FROM TH_CONSULT B1 UNION ALL SELECT B2.* FROM TH_CONSULT_HIST B2) B ,IF_CO_USER_MA C WHERE 1=1 AND A.CNLT_SEQ = B.CNLT_SEQ AND A.USER_ID = C.USER_ID
AND B.CUST_NM LIKE '%'|| #{selCustNm} ||'%'
AND A.TELL_NUM LIKE '%'|| #{selTellNum} ||'%'
AND C.USER_NM LIKE '%'|| #{selUserNm} ||'%'
AND A.CHNL_CODE = #{selChnlCode}
AND A.LCODE = #{selLcode}
AND A.MCODE = #{selMcode}
AND A.SCODE = #{selScode}
AND A.CNLT_PROC_CD = #{selCnltProcCd}
AND A.CNLT_DATE BETWEEN TO_DATE(#{selFromDt},'YYYY-MM-DD') AND (TO_DATE(#{selToDt},'YYYY-MM-DD') +1)
ORDER BY A.CNLT_DATE DESC
